function transactionDB(e){var i=window.openDatabase(DBname,DBversion,DBdisname,DBsize);i.transaction(e,errorDB)}function CreatQuery(e){e.executeSql("CREATE TABLE IF NOT EXISTS slist (id INTEGER PRIMARY KEY AUTOINCREMENT,newinstall varchar, highestscore Integer, fblogin varchar)",[],function(e){e.executeSql('INSERT INTO slist (id,newinstall,highestscore,fblogin) SELECT "1", "YES", "0", "NULL" WHERE NOT EXISTS (SELECT id FROM slist WHERE id = "1") ',[],function(){})},errorDB)}function UpdateHighest(e){var i=window.openDatabase(DBname,DBversion,DBdisname,DBsize);i.transaction(function(i){i.executeSql('SELECT highestscore FROM slist WHERE id="1"',[],function(i,s){s.rows.item(0).highestscore<e&&i.executeSql('UPDATE slist SET highestscore="'+e+'" WHERE id="1"')})})}function GetHighestScore(){var e=window.openDatabase(DBname,DBversion,DBdisname,DBsize);e.transaction(function(e){e.executeSql('SELECT highestscore FROM slist WHERE id="1"',[],function(e,i){$("#highestscore").text("Your Highest Score: "+i.rows.item(0).highestscore)})})}function errorDB(e){alert("Error processing SQL: "+e.message)}function successDB(){}const DBname="calculateDB",DBversion="1,0;",DBdisname="listDB",DBsize=4e6;